import React, {useState} from 'react'
import { axiosInstance } from '../index';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeartBroken, faHeart } from '@fortawesome/free-solid-svg-icons'

import { Card, Button } from 'react-bootstrap';
import { useSelector } from 'react-redux';

const PostItem = props => {
    const {
        postId,
        title,
        content,
        author,
        likes,
        dislikes,
        userLikes,
        userDislikes,
        userId,
        token
    } = props

    const [liked, setLiked] = useState([])
    const [disliked, setDisliked] = useState([])

    const likePost = () => {
        axiosInstance.patch('/posts/like/' + postId, {
            userId: userId,
            token: token,
            postId: postId
        }).then(() => {
            // getLikedAndDisliked()
        })
    }

    const dislikePost = () => {
        axiosInstance.patch('/posts/dislike/' + postId, {
            userId: userId,
            token: token,
            postId: postId
        }).then(() => {
            // getLikedAndDisliked()
        })
    }

    return (
        <div>
            <Card>
                <Card.Header>{title}</Card.Header>
                <Card.Body>
                    <Card.Text>
                        {content}
                    </Card.Text>
                </Card.Body>
                <Card.Text>
                    <button onClick={() => likePost()} style={{backgroundColor: 'transparent', border: 'none'}}><FontAwesomeIcon icon={faHeart} color={userLikes.indexOf(postId) > -1 ? 'red' : 'gray'} /></button>
                    {likes}
                    <button onClick={() => dislikePost()} style={{backgroundColor: 'transparent', border: 'none'}}><FontAwesomeIcon icon={faHeartBroken} color={userDislikes.indexOf(postId) > -1 ? 'red' : 'gray'} /></button>
                    {dislikes}
                </Card.Text>

                <footer className="blockquote-footer" style={{ padding: '10px' }}>
                    <span>{author}</span>
                </footer>
            </Card>
        </div>
    )
}

export default PostItem
