import React, { useState } from 'react'
import { axiosInstance } from '../index';

import { connect } from 'react-redux';
import MyInfo from '../Components/myInfo'
import MyLikes from '../Components/myLikes'
import MyPosts from '../Components/myPosts'

import { ButtonGroup, Button } from 'react-bootstrap';

const AccountView = props => {
    const [pageType, setPageType] = useState('likes')

    const PageToRender = (pageType) => {
        if (pageType === 'likes') {
            return (<MyLikes />)
        } else if (pageType === 'posts') {
            return (<MyPosts />)
        } else {
            return (<MyInfo />)
        }
    }

    return (
        <div>
            <ButtonGroup aria-label="Basic example">
                <Button variant="secondary" onClick={() => setPageType('likes')}>My Likes</Button>
                <Button variant="secondary" onClick={() => setPageType('posts')}>My Posts</Button>
                <Button variant="secondary" onClick={() => setPageType('account')}>My Account</Button>
            </ButtonGroup>
            {props.username}
            <PageToRender pageType={pageType} />
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        username: state.auth.username
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AccountView);
