import React, { useState, useEffect } from 'react'
import { Link, Redirect } from 'react-router-dom';
import { axiosInstance } from '../index';

import { connect } from 'react-redux';
import * as actionCreators from '../store/actions/index';
import './login.css';

import { Form, Button, InputGroup } from 'react-bootstrap';

const Login = props => {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [goToPosts, setGoToPosts] = useState(false)

    //If token can be verified then go to posts page
    useEffect(() => {
        axiosInstance.get('account/verify?token=' + props.token)
            .then(response => {
                if (response.data.success) {
                    console.log('go to post')
                    setGoToPosts(true)
                } else {
                    console.log('error verifying account')
                }
            });
    }, [props.token])


    const submit = () => {
        if (username && password) {
            console.log(username, password)
            props.login(username, password)
        }
    }

    return (
        <div className="Main">
            {goToPosts && <Redirect to='/posts' />}
            <div className="Leftside">
                Speak
                <br />
                Your
                <br />
                Mind
            </div>
            <div className="Rightside">
                <div className="LoginContainer">
                    <Form>
                        <Form.Group controlId="formBasicEmail">
                            <Form.Label>Username</Form.Label>
                            <Form.Control type="text" placeholder="Enter Username" />
                        </Form.Group>

                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Enter Password" />
                        </Form.Group>
                        <Button variant="primary" type="submit" style={{ backgroundColor: '#8093F1', border: 'none' }}>
                            Sign In
                        </Button>
                        <Form.Text className="text-muted" style={{ marginTop: '2%'}}>
                            <Link to="/sign-up"><a href="/sign-up" classname="ForgetPwd">Sign Up</a></Link>
                        </Form.Text>
                    </Form>
                </div>

                {/* <div classname="col-md-6 login-form-1">
                    <h3>Sign In</h3>
                    <form>
                        <div classname="form-group">
                            <input type="text" classname="form-control" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
                        </div>
                        <div classname="form-group">
                            <input type="password" classname="form-control" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                        </div>
                        <div classname="form-group">
                            <input type="submit" classname="btnSubmit" value="Login" onClick={() => submit()} />
                        </div>
                        <div classname="form-group">
                            <Link to="/sign-up"><a href="/sign-up" classname="ForgetPwd">Sign Up</a></Link>
                        </div>
                    </form>
                </div> */}
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        token: state.auth.token
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        login: (username, password) => dispatch(actionCreators.login(username, password))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
