import React, { useState, useEffect } from 'react'
import { Link, Redirect } from 'react-router-dom';
import { axiosInstance } from '../index';

import { connect } from 'react-redux';
import * as actionCreators from '../store/actions/index';
import './login.css'

const Login = props => {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [goToPosts, setGoToPosts] = useState(false)

    //If token can be verified then go to posts page
    useEffect(() => {
        axiosInstance.get('account/verify?token=' + props.token)
            .then(response => {
                if (response.data.success) {
                    console.log('go to post')
                    setGoToPosts(true)
                } else {
                    console.log('error verifying account')
                }
            });
    }, [props.token])


    const submit = () => {
        if (username && password) {
            console.log(username, password)
            props.login(username, password)
        }
    }

    return (
        <div class="main">
            <div class="left-side">
                Speak
                <br />
                Your
                <br />
                Mind
            </div>
            <div class="right-side">
                {goToPosts && <Redirect to='/posts' />}
                <div class="col-md-6 login-form-1">
                    <h3>Sign In</h3>
                    <form>
                        <div class="form-group">
                            <input type="text" class="form-control" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)}/>
                        </div>
                        <div class="form-group">
                            <input type="password" class="form-control" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)}/>
                        </div>
                        <div class="form-group">
                            <input type="submit" class="btnSubmit" value="Login" onClick={() => submit()}/>
                        </div>
                        <div class="form-group">
                        <Link to="/sign-up"><a href="/sign-up" class="ForgetPwd">Sign Up</a></Link>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        token: state.auth.token
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        login: (username, password) => dispatch(actionCreators.login(username, password))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
