import React, {useState} from 'react'
import { axiosInstance } from '../index';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeartBroken, faHeart } from '@fortawesome/free-solid-svg-icons'

import { Card, Button } from 'react-bootstrap';
import { useSelector } from 'react-redux';

const PostItem = props => {
    const {
        postId,
        title,
        content,
        author,
        likes,
        dislikes,
        userLikes,
        userDislikes,
        userId,
        token
    } = props

    const [liked, setLiked] = useState([])
    const [unLiked, setUnLiked] = useState([])
    const [disliked, setDisliked] = useState([])
    const [unDisliked, setUnDisliked] = useState([])

    const likePost = () => {
        axiosInstance.patch('/posts/like/' + postId, {
            userId: userId,
            token: token,
            postId: postId
        }).then(() => {
            // getLikedAndDisliked()
        })
        if (liked.indexOf(postId) > -1) {
            let newArray = liked.filter(val => val !== postId)
            setLiked(newArray)
        } else {
            setLiked([...liked, postId])
        }
    }

    const dislikePost = () => {
        axiosInstance.patch('/posts/dislike/' + postId, {
            userId: userId,
            token: token,
            postId: postId
        }).then(() => {
            // getLikedAndDisliked()
        })
        if (disliked.indexOf(postId) > -1) {
            let newArray = disliked.filter(val => val !== postId)
            setDisliked(newArray)
        } else {
            setDisliked([...disliked, postId])
        }
    }

    const isLiked = () => {
        if (userLikes.indexOf(postId) > -1 || liked.indexOf(postId) > -1) {
            return 'red'
        }
        return 'gray'
    }

    const isDisliked = () => {
        if (userDislikes.indexOf(postId) > -1 || disliked.indexOf(postId) > -1) {
            return 'red'
        }
        return 'gray'
    }

    return (
        <div>
            <Card>
                <Card.Header>{title}</Card.Header>
                <Card.Body>
                    <Card.Text>
                        {content}
                    </Card.Text>
                </Card.Body>
                <Card.Text>
                    <button onClick={() => likePost()} style={{backgroundColor: 'transparent', border: 'none'}}><FontAwesomeIcon icon={faHeart} color={isLiked()} /></button>
                    {likes}
                    <button onClick={() => dislikePost()} style={{backgroundColor: 'transparent', border: 'none'}}><FontAwesomeIcon icon={faHeartBroken} color={isDisliked()} /></button>
                    {dislikes}
                </Card.Text>

                <footer className="blockquote-footer" style={{ padding: '10px' }}>
                    <span>{author}</span>
                </footer>
            </Card>
        </div>
    )
}

export default PostItem
