import React, { useState } from 'react'
import { axiosInstance } from '../index';
import { Link, Redirect } from 'react-router-dom';

import './signUp.css'

const SignUp = () => {
    const [username, setUsername] = useState()
    const [password, setPassword] = useState()
    const [goToLogin, setGoToLogin] = useState(false)

    const submit = () => {
        if (username && password && username.length > 3 && password.length > 6) {
            axiosInstance.post('account/create', {
                username: username,
                password: password,
            }).then(response => {
                if (response.data.success) {
                    setUsername('')
                    setPassword('')
                    setGoToLogin(true)
                } else {
                    console.log('error creating account')
                }
            });
        }
    }

    return (
        // <div>
        //     {goToLogin && <Redirect to='/' />}
        //     username
        //     <input type="text" onChange={(e) => setUsername(e.target.value)} />
        //     <br />
        //     <br />
        //     <br />
        //     password
        //     <input type="password" onChange={(e) => setPassword(e.target.value)} />
        //     <br />
        //     <br />
        //     <br />
        //     <button onClick={() => submit()} >sign up</button>
        // </div>

        <div class="main">
            <div class="left-side">
                Speak
                <br />
                Your
                <br />
                Mind
            </div>
            <div class="right-side">
                {goToPosts && <Redirect to='/posts' />}
                <div class="col-md-6 login-form-1">
                    <h3>Sign In</h3>
                    <form>
                        <div class="form-group">
                            <input type="text" class="form-control" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
                        </div>
                        <div class="form-group">
                            <input type="password" class="form-control" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                        </div>
                        <div class="form-group">
                            <input type="submit" class="btnSubmit" value="Login" onClick={() => submit()} />
                        </div>
                        <div class="form-group">
                            <a href="#" class="ForgetPwd"><Link to="/sign-up">Sign Up</Link></a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default SignUp
