import React, { useState, useEffect } from 'react'
import { Link, Redirect } from 'react-router-dom';
import { axiosInstance } from '../index';

import { connect } from 'react-redux';
import * as actionCreators from '../store/actions/index';

import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from 'mdbreact';

const Login = props => {
    const [username, setUsername] = useState()
    const [password, setPassword] = useState()
    const [goToPosts, setGoToPosts] = useState(false)

    //If token can be verified then go to posts page
    useEffect(() => {
        axiosInstance.get('account/verify?token=' + props.token)
            .then(response => {
                if (response.data.success) {
                    console.log('go to post')
                    setGoToPosts(true)
                } else {
                    console.log('error verifying account')
                }
            });
    }, [props.token])


    const submit = () => {
        if (username && password) {
            props.login(username, password)
        }
    }

    return (
        <div>
            {goToPosts && <Redirect to='/posts' />}
            login
            username
            {/* <MDBInput type="text" label="Username" icon="envelope" outline onChange={(e) => setUsername(e.target.value)} /> */}
            <MDBContainer>
                <MDBRow>
                    <MDBCol md="6">
                        <form>
                            <p className="h5 text-center mb-4">Sign in</p>
                            <div className="grey-text">
                                {/* <MDBInput label="Type your email" icon="envelope" group type="email" validate error="wrong" success="right" />
                                <MDBInput label="Type your password" icon="lock" group type="password" validate /> */}
                            </div>
                            <div className="text-center">
                                {/* <MDBBtn>Login</MDBBtn> */}
                            </div>
                        </form>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
            <br />
            <br />
            <br />
            password
            <input type="password" onChange={(e) => setPassword(e.target.value)} />
            <br />
            <br />
            <br />
            <button onClick={() => submit()} >login</button>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        token: state.auth.token
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        login: (username, password) => dispatch(actionCreators.login(username, password))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
